import React, { useState } from "react";
import { UncontrolledAccordionBody } from "./UncontrolledAccordionBody";
import { UncontrolledAccordionTitle } from "./UncontrolledAccordionTitle";

type AccordionType = {
    title?: string;
    collapsed?: boolean;
}

 export const UncontrolledAccordion = (props: AccordionType) => { 
    console.log("Accordion rendering")

    let [collapsed, collapsedSwitch] = useState <boolean>(false); // hook

    const buttonSTyle = {
      width: "130px",
      height: "20px",
      border: "1px solid black",
      display: "inline-block",
      padding: "2px",
      backgroundColor: collapsed ? "gray" : "white"
  };

      return (
        <>

          <div onClick={ () => { collapsedSwitch(collapsed)} }  > 
            <button style={buttonSTyle}>{props.title}</button>
          </div>

          <UncontrolledAccordionTitle header={props.title} /> 
          { collapsed &&  <UncontrolledAccordionBody /> }

        </>
      );
};
