import React from "react";

type OnOffType = {
    on: boolean;
}

export const OnOff = (props: OnOffType) => {

    const onSTyle = {};
    const offSTyle = {}; 
    const indicatorSTyle = {
        width: "30px",
        height: "30px",
        borderRadius: "15px",
        border: "1px solid black"
    };   

    return (
        <div>
            <div></div>
            <div></div>
            <div style={indicatorSTyle}></div>

    {props.on && <button>On</button>}
    {!props.on && <button>Off</button>}

        </div>
    )


}
